import React, { useState, useEffect, useMemo } from 'react';
import { getSummary } from '../services/api';
import { useReactTable, getCoreRowModel } from '@tanstack/react-table';

function Dashboard() {
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const response = await getSummary();
        setSummary(response.data);
      } catch (err) {
        setError('Failed to fetch summary');
      }
    };
    fetchSummary();
  }, []);

  // Transform summary data into an array for React Table
  const data = useMemo(() => {
    if (!summary) return [];
    return [
      { key: 'Total Count', value: summary.count },
      { key: 'Average Flowrate', value: summary.avg_flowrate },
      { key: 'Average Pressure', value: summary.avg_pressure },
      { key: 'Average Temperature', value: summary.avg_temp },
      { key: 'Type Distribution', value: JSON.stringify(summary.type_distribution, null, 2) }, // Display as formatted JSON string
    ];
  }, [summary]);

  // Define columns for the table
  const columns = useMemo(
    () => [
      {
        accessorKey: 'key',
        header: 'Metric',
      },
      {
        accessorKey: 'value',
        header: 'Value',
        cell: ({ getValue }) => {
          const value = getValue();
          // If the value is a JSON string (for type_distribution), render it in a pre tag for better formatting
          return typeof value === 'string' && value.startsWith('{') ? (
            <pre style={{ whiteSpace: 'pre-wrap', margin: 0 }}>{value}</pre>
          ) : (
            value
          );
        },
      },
    ],
    []
  );

  // Initialize the table instance
  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
  });

  if (error) return <p>{error}</p>;
  if (!summary) return <p>Loading...</p>;

  return (
    <div>
      <h2>Dashboard Summary</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          {table.getHeaderGroups().map((headerGroup) => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map((header) => (
                <th
                  key={header.id}
                  style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}
                >
                  {header.isPlaceholder ? null : header.renderHeader()}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id} style={{ border: '1px solid #ddd', padding: '8px' }}>
                  {cell.renderCell()}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;


