import React, { useState, useEffect } from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import { getSummary } from '../services/api';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale);

function Visualizations() {
  const [data, setData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getSummary();
        setData(response.data);
      } catch (err) {
        setError('Failed to fetch data');
      }
    };
    fetchData();
  }, []);

  if (error) return <p>{error}</p>;
  if (!data) return <p>Loading...</p>;

  // Dynamic colors for pie chart
  const generateColors = (num) => {
    const colors = ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'];
    return Array.from({ length: num }, (_, i) => colors[i % colors.length]);
  };

  const pieData = {
    labels: Object.keys(data.type_distribution),
    datasets: [
      {
        data: Object.values(data.type_distribution),
        backgroundColor: generateColors(Object.keys(data.type_distribution).length),
      },
    ],
  };

  const barData = {
    labels: ['Avg Flowrate', 'Avg Pressure', 'Avg Temp'],
    datasets: [
      {
        label: 'Averages',
        data: [data.avg_flowrate, data.avg_pressure, data.avg_temp],
        backgroundColor: '#36A2EB',
      },
    ],
  };

  return (
    <div>
      <h2>Visualizations</h2>
      <div style={{ width: '400px', marginBottom: '20px' }}>
        <h3>Type Distribution</h3>
        <Pie data={pieData} />
      </div>
      <div style={{ width: '400px' }}>
        <h3>Averages</h3>
        <Bar data={barData} />
      </div>
    </div>
  );
}

export default Visualizations;


