import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8000/api/',  // Adjust if backend port differs
  headers: { 'Content-Type': 'multipart/form-data' }  // For file uploads
});

// Add authentication if implemented in backend (e.g., token from localStorage)
// api.interceptors.request.use(config => {
//   const token = localStorage.getItem('token');
//   if (token) config.headers.Authorization = `Token ${token}`;
//   return config;
// });

export const uploadCSV = (file) => {
  const formData = new FormData();
  formData.append('file', file);
  return api.post('upload/', formData);
};

export const getSummary = () => api.get('summary/');

export const getHistory = () => api.get('history/');

// Add more endpoints if needed, e.g., for PDF report: export const getPDF = () => api.get('report/', { responseType: 'blob' });
