// Updated Dashboard.js (added auth prompt, PDF download, fixed field names, added Bar chart for averages)
import React, { useState, useEffect, useMemo } from 'react';
import { getSummary } from '../services/api';
import { useReactTable, getCoreRowModel } from '@tanstack/react-table';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

function Dashboard() {
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState('');
  const [authSet, setAuthSet] = useState(false);

  useEffect(() => {
    const username = localStorage.getItem('username');
    const password = localStorage.getItem('password');
    if (!username || !password) {
      const user = prompt('Enter username:');
      const pass = prompt('Enter password:');
      if (user && pass) {
        localStorage.setItem('username', user);
        localStorage.setItem('password', pass);
        const token = btoa(`${user}:${pass}`);
        axios.defaults.headers.common['Authorization'] = `Basic ${token}`;
        setAuthSet(true);
      } else {
        setError('Authentication required');
        return;
      }
    } else {
      const token = btoa(`${username}:${password}`);
      axios.defaults.headers.common['Authorization'] = `Basic ${token}`;
      setAuthSet(true);
    }
  }, []);

  useEffect(() => {
    if (authSet) {
      const fetchSummary = async () => {
        try {
          const response = await getSummary();
          setSummary(response.data);
        } catch (err) {
          setError('Failed to fetch summary: ' + (err.response?.data?.error || err.message));
        }
      };
      fetchSummary();
    }
  }, [authSet]);

  const downloadPDF = async () => {
    try {
      const res = await axios.get('/api/pdf/', { responseType: 'blob' }); // Adjust path if needed
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.download = 'report.pdf';
      link.click();
    } catch (error) {
      alert('Failed to download PDF: ' + (error.response?.data?.error || error.message));
    }
  };

  // Transform summary data into an array for React Table
  const data = useMemo(() => {
    if (!summary) return [];
    return [
      { key: 'Total Count', value: summary.total_count },
      { key: 'Average Flowrate', value: summary.avg_flowrate },
      { key: 'Average Pressure', value: summary.avg_pressure },
      { key: 'Average Temperature', value: summary.avg_temperature },
      { key: 'Type Distribution', value: JSON.stringify(summary.type_distribution, null, 2) }, // Display as formatted JSON string
    ];
  }, [summary]);

  // Define columns for the table
  const columns = useMemo(
    () => [
      {
        accessorKey: 'key',
        header: 'Metric',
      },
      {
        accessorKey: 'value',
        header: 'Value',
        cell: ({ getValue }) => {
          const value = getValue();
          // If the value is a JSON string (for type_distribution), render it in a pre tag for better formatting
          return typeof value === 'string' && value.startsWith('{') ? (
            <pre style={{ whiteSpace: 'pre-wrap', margin: 0 }}>{value}</pre>
          ) : (
            value
          );
        },
      },
    ],
    []
  );

  // Initialize the table instance
  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
  });

  if (error) return <p>{error}</p>;
  if (!authSet) return <p>Setting up authentication...</p>;
  if (!summary) return <p>Loading...</p>;

  const barData = {
    labels: ['Flowrate', 'Pressure', 'Temperature'],
    datasets: [{ label: 'Averages', data: [summary.avg_flowrate, summary.avg_pressure, summary.avg_temperature], backgroundColor: '#4a90e2' }]
  };

  return (
    <div>
      <h2>Dashboard Summary</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          {table.getHeaderGroups().map((headerGroup) => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map((header) => (
                <th
                  key={header.id}
                  style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}
                >
                  {header.isPlaceholder ? null : header.column.columnDef.header}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map((row) => (
            <tr key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <td key={cell.id} style={{ border: '1px solid #ddd', padding: '8px' }}>
                  {cell.renderValue()}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ marginTop: '20px', width: '400px' }}>
        <h3>Averages Chart</h3>
        <Bar data={barData} options={{ scales: { y: { beginAtZero: true } } }} />
      </div>
      <button onClick={downloadPDF} style={{ marginTop: '20px' }}>Download PDF Report</button>
    </div>
  );
}

export default Dashboard;


